import React from "react";
import "./.css"; // Import your custom CSS file
import { Modal, ModalBody, ModalHeader } from "reactstrap"; // Ensure you have reactstrap installed

export const Team = (props) => {
  const [modal, setModal] = React.useState(false);
  const [selectedMember, setSelectedMember] = React.useState(null);

  const toggleModal = () => setModal(!modal);

  const openModal = (member) => {
    setSelectedMember(member);
    toggleModal();
  };

  return (
    <div id="team" className="text-center">
      <div className="container">
        <div className="col-md-8 col-md-offset-2 section-title">
          <h2>Meet the Team</h2>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit duis sed
            dapibus leonec.
          </p>
        </div>
        <div className="row">
          {props.data
            ? props.data.map((d, i) => (
                <div key={`${d.name}-${i}`} className="col-md-3 col-sm-6 team">
                  <div className="thumbnail" onClick={() => openModal(d)}>
                    <img src={d.img} alt={d.name} className="team-img" />
                    <div className="caption">
                      <h4>{d.name}</h4>
                      <p>{d.job}</p>
                    </div>
                  </div>
                </div>
              ))
            : "loading"}
        </div>

        {/* Modal for team member details */}
        {selectedMember && (
          <Modal isOpen={modal} toggle={toggleModal}>
            <ModalHeader toggle={toggleModal}>{selectedMember.name}</ModalHeader>
            <ModalBody>
              <img src={selectedMember.img} alt={selectedMember.name} className="img-fluid" />
              <p>{selectedMember.job}</p>
              <p>{selectedMember.description || "No additional details available."}</p>
            </ModalBody>
          </Modal>
        )}
      </div>
    </div>
  );
};
