import React, { useEffect, useRef, useState } from 'react';

const About = () => {
  const videoRef = useRef(null); // Create a reference to the video element
  const [isMuted, setIsMuted] = useState(true); // State to manage if the video is muted or not

  useEffect(() => {
    const videoElement = videoRef.current;

    // Intersection Observer callback
    const observerCallback = (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          videoElement.play(); // Autoplay when in view
        } else {
          videoElement.pause(); // Pause when out of view
        }
      });
    };

    // Set up the IntersectionObserver
    const observerOptions = {
      root: null, // The viewport
      threshold: 0.5, // Play when 50% of the video is visible
    };

    const observer = new IntersectionObserver(observerCallback, observerOptions);
    if (videoElement) {
      observer.observe(videoElement); // Observe the video element
    }

    return () => {
      if (videoElement) {
        observer.unobserve(videoElement); // Clean up the observer on component unmount
      }
    };
  }, []);

  // Function to toggle sound on/off
  const toggleSound = () => {
    const videoElement = videoRef.current;
    videoElement.muted = !isMuted; // Toggle the muted property
    setIsMuted(!isMuted); // Update state to reflect the current mute status
  };

  return (
    <section id="about" className="about">
      <div className="about-container">
        {/* Video Section */}
        <div className="about-video">
          <video ref={videoRef} width="100%" height="315" muted={isMuted}>
            <source src="/img/crownprincetvid.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>

          {/* Sound Toggle Button */}
          <button onClick={toggleSound} className="sound-toggle-btn">
            {isMuted ? 'Unmute' : 'Mute'}
          </button>
        </div>

        {/* Text Section */}
        <div className="about-text">
          <h2>Welcome to <span>Terra Developers</span></h2>
          <p>
            Terra Developers presents a world of luxurious properties that combine comfort, style, and functionality. As a leading real estate development company in Nigeria, we bring years of expertise to create apartments that wow our buyers and investors.
          </p>
          <p>
            With extra focus on functionality, efficiency, and unparalleled customer experience, our homes are designed to cater to the diverse needs of our esteemed clients.
          </p>
          <p>
            At Terra, we take pride in ensuring you <span>live the good life!</span>
          </p>
        </div>
      </div>
    </section>
  );
};

export default About;
