import React, { useEffect, useRef, useState } from 'react';

const About = () => {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(true);

  useEffect(() => {
    const videoElement = videoRef.current;

    const observerCallback = (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          videoElement.play();
        } else {
          videoElement.pause();
        }
      });
    };

    const observerOptions = {
      root: null,
      threshold: 0.5,
    };

    const observer = new IntersectionObserver(observerCallback, observerOptions);
    if (videoElement) {
      observer.observe(videoElement);
    }

    return () => {
      if (videoElement) {
        observer.unobserve(videoElement);
      }
    };
  }, []);

  const toggleSound = () => {
    const videoElement = videoRef.current;
    videoElement.muted = !isMuted;
    setIsMuted(!isMuted);
  };

  return (
    <section id="about" className="about">
      <div className="about-container">
        {/* Video Section */}
        <div className="about-video">
          <video
            ref={videoRef}
            width="100%"
            height="315"
            muted={isMuted}
            className="video-with-border" // Add a class for styling the border
          >
            <source src="/img/crownprincetvid.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>

          {/* Sound Toggle Button */}
          <button onClick={toggleSound} className="sound-toggle-btn">
            {isMuted ? 'Unmute' : 'Mute'}
          </button>
        </div>

        {/* Text Section */}
        <div className="about-text">
          <h2>About Us</h2> {/* Added About Us heading */}
          <h3>Welcome to <span>Terra Developers</span></h3>
          <p>
            Terra Developers presents a world of luxurious properties that combine comfort, style, and functionality. As a leading real estate development company in Nigeria, we bring years of expertise to create apartments that wow our buyers and investors.
          </p>
          <p>
            With extra focus on functionality, efficiency, and unparalleled customer experience, our homes are designed to cater to the diverse needs of our esteemed clients.
          </p>
          <p>
            At Terra, we take pride in ensuring you <span>live the good life!</span>
          </p>
        </div>
      </div>
    </section>
  );
};

export default About;
