import React, { useState } from 'react';

const properties = [
  {
    id: 1,
    title: "Carmen's Creek",
    description: 'FROM ₦40,000,000',
    image: '/img/ff6.jpg',
  },
  {
    id: 2,
    title: "Paul's Creek",
    description: 'FROM ₦85,000,000',
    image: '/img/ff4.jpg',
  },
  {
    id: 3,
    title: "Zaarah's Place",
    description: 'FROM ₦29,000,000',
    image: '/img/ff3.jpg',
  },
  {
    id: 4,
    title: "Eric's Park",
    description: 'FROM ₦65,000,000',
    image: '/img/ff2.jpg',
  },
  {
    id: 5,
    title: "Nathan's Bay",
    description: 'FROM ₦27,000,000',
    image: '/img/ff1.jpg',
  },
  {
    id: 5,
    title: "Nathan's Bay",
    description: 'FROM ₦27,000,000',
    image: '/img/ff1.jpg',
  },
  {
    id: 5,
    title: "Nathan's Bay",
    description: 'FROM ₦27,000,000',
    image: '/img/ff1.jpg',
  },
  {
    id: 5,
    title: "Nathan's Bay",
    description: 'FROM ₦27,000,000',
    image: '/img/ff1.jpg',
  },
];

const PropertyListing = () => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const scrollAmount = 300; // Scroll distance in pixels
  const cardWidth = 300; // Assuming each card has a width of 300px
  const totalWidth = properties.length * cardWidth;

  // Scroll to the left
  const scrollLeft = () => {
    setScrollPosition((prev) => Math.max(prev - scrollAmount, 0));
  };

  // Scroll to the right
  const scrollRight = () => {
    setScrollPosition((prev) => Math.min(prev + scrollAmount, totalWidth - 300)); // 300 is the width of the visible area
  };

  return (
    <section id="property-listing" className="property-listing">
      <div className="container">
        {/* Explore Our Projects Heading */}
        <div className="explore-section">
          <h2 className="explore-heading">Explore <span>Our</span> Projects</h2>
          <p className="section-subtitle">
            Embrace a life of comfort, convenience, and sophistication as you choose the perfect apartment that resonates with your vision of a truly exceptional living experience.
          </p>
        </div>

        <div className="property-cards-container" style={{ position: 'relative' }}>
          <div className="scroll-arrow left-arrow" onClick={scrollLeft} aria-label="Scroll left">
            &#8592;
          </div>

          <div
            className="property-cards"
            style={{
              display: 'flex',
              overflow: 'hidden',
              transform: `translateX(-${scrollPosition}px)`,
              transition: 'transform 0.3s ease',
            }}
          >
            {properties.map((property) => (
              <div className="property-card" key={property.id} style={{ width: `${cardWidth}px` }}>
                <div className="image-container">
                  <img src={property.image} alt={property.title} loading="lazy" />
                  <div className="explore-overlay">
                    <button className="explore-btn">Explore</button>
                  </div>
                </div>
                <h3>{property.title}</h3>
                <p>{property.description}</p>
              </div>
            ))}
          </div>

          <div className="scroll-arrow right-arrow" onClick={scrollRight} aria-label="Scroll right">
            &#8594;
          </div>
        </div>
      </div>
    </section>
  );
};

export default PropertyListing;
