import React from 'react';
import styled from 'styled-components';

const BlogWrapper = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
  padding: 50px;
`;

const BlogCard = styled.div`
  background-color: #f9f9f9;
  padding: 20px;
  border: 1px solid #e0e0e0;
  text-align: left;
  cursor: pointer;
  transition: transform 0.2s ease-in-out;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);

  &:hover {
    transform: scale(1.05);
  }

  .date {
    font-size: 2rem;
    color: #c87030;
    font-weight: bold;
  }

  .title {
    font-size: 1.2rem;
    margin: 10px 0;
    color: #333;
    font-weight: bold;
  }

  .category {
    font-size: 0.9rem;
    color: #888;
  }

  .month {
    font-size: 0.9rem;
    color: #888;
  }
`;

const BlogPage = () => {
  const blogs = [
    {
      category: 'Uncategorized',
      title: '10 Shocking Real Estate Myths in Nigeria (Debunked)',
      date: '10',
      month: 'Jun',
    },
    {
      category: 'Building',
      title: 'Building Your House: 8 Stylish Building Materials',
      date: '06',
      month: 'Aug',
    },
    {
      category: 'Investment',
      title: 'Factors to Consider Before Buying Properties in Nigeria',
      date: '24',
      month: 'Jul',
      darkCard: true,
    },
    {
      category: 'Charity',
      title: 'Terra Gives: Nurturing Hope, Uplifting Lives',
      date: '',
      month: '',
    },
  ];

  return (
    <BlogWrapper>
      {blogs.map((blog, index) => (
        <BlogCard key={index} style={blog.darkCard ? { backgroundColor: '#333', color: '#fff' } : {}}>
          <div className="date">{blog.date}</div>
          <div className="title">{blog.title}</div>
          <div className="category">{blog.category}</div>
          <div className="month">{blog.month}</div>
        </BlogCard>
      ))}
    </BlogWrapper>
  );
};

export default BlogPage;
