import React, { useState, useRef } from 'react';
import Navbar from './components/navbar';
import Hero from './components/hero';
import About from './components/about';
import Services from './components/services';
import Testimonials from './components/testimonials';
import PropertyListing from './components/propertylisting';
import Team from './components/team';
import Blog from './components/blog';
import FindAgent from './components/findagent';
import Contact from './components/contact';
import ParallaxSection from './components/parallaxsection';
import Footer from './components/footer';
import SignUp from './components/signup'; // Import the SignUp component
import './index.css';

function App() {
  const [isSignUpVisible, setSignUpVisible] = useState(false); // State to handle form visibility

  // Create refs for each section
  const aboutRef = useRef(null);
  const servicesRef = useRef(null);
  const testimonialsRef = useRef(null);
  const propertyListingRef = useRef(null);
  const contactRef = useRef(null);

  // Function to handle scroll to specific section
  const scrollToSection = (sectionRef) => {
    if (sectionRef.current) {
      sectionRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleRegisterClick = () => {
    setSignUpVisible(true);
  };

  const handleCloseForm = () => {
    setSignUpVisible(false);
  };

  return (
    <div className="App">
      {/* Pass the scrollToSection function to the Navbar */}
      <Navbar 
        onRegisterClick={handleRegisterClick} 
        onScrollToSection={scrollToSection}
        refs={{ aboutRef, servicesRef, testimonialsRef, propertyListingRef, contactRef }}
      />

      <Hero />
      
      {/* Pass ref to each section */}
      <section ref={aboutRef}>
        <About />
      </section>
      
      <section ref={servicesRef}>
        <Services />
      </section>
      
      <section ref={testimonialsRef}>
        <Testimonials />
      </section>
      
      <section ref={propertyListingRef}>
        <PropertyListing />
      </section>
       <section ref={propertyListingRef}>
        <PropertyListing />
      </section>
      
      <section ref={contactRef}>
        <Contact />
      </section>

      <ParallaxSection />
      <Footer />

      {/* Render SignUp form only if visible */}
      {isSignUpVisible && <SignUp onClose={handleCloseForm} />}
    </div>
  );
}

export default App;
