import React, { useState } from 'react';

const properties = [
  {
    id: 1,
    title: "Carmen's Creek",
    description: 'FROM ₦40,000,000',
    image: '/img/ff6.jpg',
  },
  {
    id: 2,
    title: "Paul's Creek",
    description: 'FROM ₦85,000,000',
    image: '/img/ff4.jpg',
  },
  {
    id: 3,
    title: "Zaarah's Place",
    description: 'FROM ₦29,000,000',
    image: '/img/ff3.jpg',
  },
  {
    id: 4,
    title: "Eric's Park",
    description: 'FROM ₦65,000,000',
    image: '/img/ff2.jpg',
  },
  {
    id: 5,
    title: "Eric's Park",
    description: 'FROM ₦65,000,000',
    image: '/img/ff2.jpg',
  },
  {
    id: 6,
    title: "Eric's Park",
    description: 'FROM ₦65,000,000',
    image: '/img/ff2.jpg',
  },
  {
    id: 7,
    title: "Eric's Park",
    description: 'FROM ₦65,000,000',
    image: '/img/ff2.jpg',
  },
  {
    id: 4,
    title: "Eric's Park",
    description: 'FROM ₦65,000,000',
    image: '/img/ff3.jpg',
  },
  {
    id: 5,
    title: "Nathan's Bay",
    description: 'FROM ₦27,000,000',
    image: '/img/ff3.jpg',
  },
  // Add more unique properties if needed
];

const PropertyListing = () => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const scrollAmount = 300; // The amount to scroll by

  // Scroll to the left
  const scrollLeft = () => {
    setScrollPosition((prevPosition) => Math.max(prevPosition - scrollAmount, 0));
  };

  // Scroll to the right
  const scrollRight = () => {
    setScrollPosition((prevPosition) => prevPosition + scrollAmount);
  };

  return (
    <section id="property-listing" className="property-listing">
      <div className="container">
        {/* Explore Our Projects Heading */}
        <div className="explore-section">
          <h2 className="explore-heading">Explore <span>Our</span> Projects</h2>
          <p className="section-subtitle">
            Embrace a life of comfort, convenience, and sophistication as you choose the perfect apartment that resonates with your vision of a truly exceptional living experience.
          </p>
        </div>

        <div className="property-cards-container" style={{ position: 'relative', overflow: 'hidden' }}>
          {/* Left Scroll Button */}
          <div
            className="scroll-arrow left-arrow"
            onClick={scrollLeft}
            style={{
              position: 'absolute',
              left: '10px',
              top: '50%',
              transform: 'translateY(-50%)',
              fontSize: '2rem',
              cursor: 'pointer',
              zIndex: 10,
              background: 'rgba(0, 0, 0, 0.5)',
              color: 'white',
              padding: '10px',
              borderRadius: '50%',
            }}
          >
            &#8592;
          </div>

          {/* Cards */}
          <div
            className="property-cards"
            style={{
              display: 'flex',
              transform: `translateX(-${scrollPosition}px)`,
              transition: 'transform 0.5s ease',
              overflow: 'hidden',
            }}
          >
            {properties.map((property) => (
              <div className="property-card" key={property.id} style={{ minWidth: '300px' }}>
                <div className="image-container">
                  <img src={property.image} alt={property.title} />
                  <div className="explore-overlay">
                    <button className="explore-btn">Explore</button>
                  </div>
                </div>
                <h3>{property.title}</h3>
                <p>{property.description}</p>
              </div>
            ))}
          </div>

          {/* Right Scroll Button */}
          <div
            className="scroll-arrow right-arrow"
            onClick={scrollRight}
            style={{
              position: 'absolute',
              right: '10px',
              top: '50%',
              transform: 'translateY(-50%)',
              fontSize: '2rem',
              cursor: 'pointer',
              zIndex: 10,
              background: 'rgba(0, 0, 0, 0.5)',
              color: 'white',
              padding: '10px',
              borderRadius: '50%',
            }}
          >
            &#8594;
          </div>
        </div>
      </div>
    </section>
  );
};

export default PropertyListing;
